      }
    },
    computed:{
      allStars(){
        return this.stars.reduce((acc, el)=> acc+ el, 0);
      }
    },
    methods:{
      togglePurchaseCompleted(){
        this.purchaseCompleted = false;
      },
      getAuth(){
        try{
          YSDK.auth.openAuthDialog().then(() => {
            initPlayer(YSDK);
          }).catch((e) => {
            console.log(e);
          });
        }catch(ignored){}

      },
      getPlayerLB(){
