            const lastAllDoneWords = dataObject.allDoneWords;
            let newWords, lastWords;
            newWords = Object.keys(allDoneWords).reduce((k, key)=> k + allDoneWords[key].length, 0);
            lastWords = Object.keys(lastAllDoneWords).reduce((k, key)=> k + lastAllDoneWords[key].length, 0);
            if(newWords > lastWords){
              change = false;
              tips = localStorage.getItem('tips');
              if(tips){
                tips = Number(tips);
              }else{
                tips = 3;
              }
              PLAYERSTATS = {
                tips: tips
              };
              setStats();
            }
          }

          PLAYESTATE = dataObject;
          if(change){
            allDoneWords = fixDoneWords(dataObject.allDoneWords);
            localStorage.setItem('allDoneWords', JSON.stringify(allDoneWords));
          }else{
            PLAYESTATE.allDoneWords = fixDoneWords(allDoneWords);
            setState();
          }
        }  else{
          playerGame.setData({
            allDoneWords: allDoneWords
          }, false).then((ignored) => {});
        }
        if(someTrue){
          update();
        }
        someTrue = true;
      }).catch((e) => {
        console.log(e);
        if(someTrue){
          update();
        }
        someTrue = true;
      });
      playerGame.getStats(['tips'], false).then((dataObject) => {
        if (dataObject.tips && change) {
          tips = dataObject.tips;
          PLAYERSTATS = dataObject;
          localStorage.setItem('tips', tips);
          setStats();
        }else{
          playerGame.setStats({
            tips: tips
          }, false).then((ignored) => {});
        }
        if(someTrue){
          update();
        }
        someTrue = true;
      }).catch((e) => {
        console.log(e);
        if(someTrue){
          update();
        }
        someTrue = true;
      });
      ysdk.getPayments({ signed: false }).then(_payments => {
