  function testStar(doneWordsLength, allWordsLength) {
    if(doneWordsLength === allWordsLength) return 3;
    let percent = Math.floor(doneWordsLength / allWordsLength * 100);
    if(percent >= 66) return 2;
    if(percent >= 33) return 1;
    return 0;
  }
  const NewAudioContext = (function() {

    try {
      window.AudioContext = window.AudioContext || window.webkitAudioContext;
      window.audioContext = new window.AudioContext();
    } catch (e) {
      console.log("No Web Audio API support");
    }
    /*
     * WebAudioAPISoundManager Constructor
     */
    var WebAudioAPISoundManager = function (context) {
      this.context = context;
      this.bufferList = {};
      this.playingSounds = {};
    };

    /*
     * WebAudioAPISoundManager Prototype
     */
    WebAudioAPISoundManager.prototype = {
      addSound: function (url) {
        // Load buffer asynchronously
        var request = new XMLHttpRequest();
        request.open("GET", url, true);
        request.responseType = "arraybuffer";

        var self = this;

        request.onload = function () {
          // Asynchronously decode the audio file data in request.response
          self.context.decodeAudioData(
                  request.response,

                  function (buffer) {
                    if (!buffer) {
                      console.log('error decoding file data: ' + url);
                      return;
                    }
                    self.bufferList[url] = buffer;
                  });
        };

        request.onerror = function () {
          console.log('BufferLoader: XHR error');
        };

        request.send();
      },
      stopSoundWithUrl: function(url) {
        if(this.playingSounds.hasOwnProperty(url)){
          for(var i in this.playingSounds[url]){
            if(this.playingSounds[url].hasOwnProperty(i)) {
              this.playingSounds[url][i].stop(0);
            }
          }
        }
      }
    };

    /*
     * WebAudioAPISound Constructor
     */
    var WebAudioAPISound = function (url, options) {
      this.settings = {
        loop: false
      };

      for(var i in options){
        if(options.hasOwnProperty(i)) {
          this.settings[i] = options[i];
        }
      }

      this.url = url + '.mp3';
      this.volume = 1;
      window.webAudioAPISoundManager = window.webAudioAPISoundManager || new WebAudioAPISoundManager(window.audioContext);
      this.manager = window.webAudioAPISoundManager;
      this.manager.addSound(this.url);
      // this.buffer = this.manager.bufferList[this.url];
    };

    /*
     * WebAudioAPISound Prototype
     */
    WebAudioAPISound.prototype = {
      play: function () {
        var buffer = this.manager.bufferList[this.url];
        //Only play if it loaded yet
        if (typeof buffer !== "undefined") {
          var source = this.makeSource(buffer);
          source.loop = this.settings.loop;
          source.start(0);

          if(!this.manager.playingSounds.hasOwnProperty(this.url)) {
            this.manager.playingSounds[this.url] = [];
          }
          this.manager.playingSounds[this.url].push(source);
        }
      },
      stop: function () {
        this.manager.stopSoundWithUrl(this.url);
      },
      getVolume: function () {
        return this.translateVolume(this.volume, true);
      },
      //Expect to receive in range 0-100
      setVolume: function (volume) {
        this.volume = this.translateVolume(volume);
      },
      translateVolume: function(volume, inverse){
        return inverse ? volume * 100 : volume / 100;
      },
      makeSource: function (buffer) {
        var source = this.manager.context.createBufferSource();
        var gainNode = this.manager.context.createGain();
        source.connect(gainNode);
        gainNode.gain.value = this.volume;
        source.buffer = buffer;
        // source.connect(gainNode);
        gainNode.connect(this.manager.context.destination);
        return source;
      }
    };

    return WebAudioAPISound;
  })();

  let wrongWordSound = new NewAudioContext('wrong-word2');
  let doneWordSound = new NewAudioContext('done-word');
  let starVolume = new NewAudioContext('star');
  let newLevel = new NewAudioContext('new-level');
  let clickSound = new NewAudioContext('click');

  const lettersMap = {
      'q' : 'й', 'w' : 'ц', 'e' : 'у', 'r' : 'к', 't' : 'е', 'y' : 'н', 'u' : 'г', 'i' : 'ш', 'o' : 'щ', 'p' : 'з', '[' : 'х', ']' : 'ъ', 'a' : 'ф', 's' : 'ы', 'd' : 'в', 'f' : 'а', 'g' : 'п', 'h' : 'р', 'j' : 'о', 'k' : 'л', 'l' : 'д', ';' : 'ж', '\'' : 'э', 'z' : 'я', 'x' : 'ч', 'c' : 'с', 'v' : 'м', 'b' : 'и', 'n' : 'т', 'm' : 'ь', ',' : 'б', '.' : 'ю','Q' : 'Й', 'W' : 'Ц', 'E' : 'У', 'R' : 'К', 'T' : 'Е', 'Y' : 'Н', 'U' : 'Г', 'I' : 'Ш', 'O' : 'Щ', 'P' : 'З', '[' : 'Х', ']' : 'Ъ', 'A' : 'Ф', 'S' : 'Ы', 'D' : 'В', 'F' : 'А', 'G' : 'П', 'H' : 'Р', 'J' : 'О', 'K' : 'Л', 'L' : 'Д', ';' : 'Ж', '\'' : 'Э', 'Z' : '?', 'X' : 'ч', 'C' : 'С', 'V' : 'М', 'B' : 'И', 'N' : 'Т', 'M' : 'Ь', ',' : 'Б', '.' : 'Ю'
  };

  function params(data) {
    try{
      // eslint-disable-next-line no-undef
      ym(57682372, 'params', data);
      // eslint-disable-next-line no-empty
    }catch(ignored){}
  }
  let iOS = /iPad|iPhone|iPod/.test(navigator.userAgent) && !window.MSStream;
  if(iOS){
    document.documentElement.addEventListener('gesturestart', function (event) {
      event.preventDefault();
    }, false);
  }
  export default {
    name: 'App',
    data(){
      return {
        levels: true,
        content: false,
        lvl: 0,
        letters: [],
        selectedLetters: [],
        wordFromLetter: '',
        numOfLevels: allWords.length,
        location: loc,
        allLocations: allLocations,
        stars: allStars,
        nowWords: [],
        doneWords: [],
        tipCount: tips,
        isBadWord: false,
        rules: isRules,
        levelsAnim: false,
        shop: false,
        newWord: '',
        animWord: '',
        words: '',
        getStar: -1,
        animWordStart: '',
        isSounds: sounds,
        advShowNow: false,
        showAdvTip: false,
        isAdvShowed: false,
        isSettings: false,
        showGameAdv: false,
        isGameAdvShow: false,
        wordWasIndex: -1
      }
    },
    computed:{
      allStars(){
        return this.stars.reduce((acc, el)=> acc+ el, 0);
      }
    },
    methods:{
      advTip(){
        if(this.tipCount > 0) return false;
        return !!(showAdv && advTime);
      },
      changeGameAdvShow(){
        this.isGameAdvShow = false;
        localStorage.setItem('isGameAdvShow', 'true');
      },
      updateAll() {
        allStars = [];
        loc = 0;
        setLoc();
        this.stars = allStars;
        this.location = loc;
        this.tipCount = tips;
        localStorage.setItem('tips', this.tipCount);
        this.isSounds = sounds;
        console.log('update');
        if (document.querySelector(".pre-download")) {
          document.querySelector(".pre-download").remove()
        }
        this.openLastLevel();
      },
      getLevel(lvl, notSound){
        if(this.isCloseLevelShow(lvl+1))return;



        if(lvl % 100 === 0){
          params({[lvl]: 1});
        }






        this.advShowNow = false;
        this.levelsAnim = false;

        if(!notSound){
          this.isGameAdvShow = isGameAdvShow;
        }

        if(this.isSounds && !notSound){
          newLevel.play();
        }

        this.getStar = -1;
        setTimeout(()=>{
          this.levels = false;
        }, 500);

        this.content = true;

        this.newWord ='';
        this.animWord ='';
        this.animWordStart = '';

        this.lvl = lvl;
        this.wordFromLetter = '';
        this.word = allWords[lvl];
        this.doneWords = allDoneWords[this.word];
        this.selectedLetters = [];

        if(this.doneWords.length === 0){

            if(lvl === 1){
                reachGoal('level2');
            }else if(lvl === 9){
                reachGoal('level10');
            }else if(lvl === 49){
                reachGoal('level50');
            } else if(lvl === 99){
                reachGoal('level100');
            }
        }

        this.letters = this.word.split('');
        this.nowWords = wordsFromWords[this.word].sort((a, b)=>{
          if(a.length > b.length) return 1;
          if(a.length < b.length) return -1;
          return 0;
        });
        setTimeout(()=>{
          document.querySelector('.words').scrollTo({
            left: 0,
            behavior: "smooth"
          });
        }, 200);
        if(showAdv && advTime){
          setTimeout(()=>{
            showAdv();
            this.advShowNow = true;
            setTimeout(()=>{
              this.advShowNow = false;
            }, 10000)
          }, 300)
        }
      },
      getLevelByLevelAndLocation(level){
        return (this.location*21) + level;
      },
      toggleSettings(){
        this.isSettings = !this.isSettings;
      },
      addBuyTips(){
        this.tipCount += TIPS;
        localStorage.setItem('tips', this.tipCount);
        PLAYERSTATS.tips = this.tipCount;
        setStats();
      },
      buyTip(item){
        buyTips(item);
      },
      sendParams(par){
        try{
          params(par);
          }catch(ignored){}

      },
      toggleRules(){
        this.rules = !this.rules;
        this.sendParams({'openRules': 1});
      },
      toggleShop(){
        this.shop = !this.shop;
      },
      switchSounds(e){
        this.isSounds = e.target.checked;
        localStorage.setItem('sounds', this.isSounds);
      },
      closeShowAdvTip(){
        this.showAdvTip = false;
      },
      isCloseLevelShow(level){
        level--;
        if(level === 0 || (lastLevel === level-1 && this.stars[lastLevel] > 0)) return false;
        return lastLevel < level;
      },
      isLevelDone(level){
        level--;
        if(lastLevel === level && this.stars[lastLevel] === 0) return false;
        return lastLevel >= level;
      },
      pressKey(e){
        if(!this.content) return;
        if(this.animWordStart !== '') return;
        let letter = e.key;
        if(letter === 'Backspace'){
          this.selectedLetters.pop();
          this.wordFromLetter = this.wordFromLetter.slice(0, -1);
        }else if(letter === 'Enter'){
          this.sendWord();
        } else{
          letter = letter.toLowerCase();
          if(lettersMap[letter]) letter = lettersMap[letter];
          for(let i = 0; i < this.letters.length; i++){
            if(letter === this.letters[i] && !this.selectedLetters.includes(i)){
              this.selectedLetters.push(i);
              this.wordFromLetter += letter;
              if(this.isSounds){
                clickSound.play();
              }
              break;
            }
          }
        }
      },
      testShowNextLevel(){
        if(this.lvl === allWords.length-1)return false;
        let nowLoc = Math.floor((this.lvl)/21);
        let nextLoc = Math.floor((this.lvl+1)/21);
        if(nowLoc !== nextLoc){
          return this.allStars >= nextLoc * 32;
        }
        return this.stars[this.lvl] > 0;
      },
      nextLevel(){
        if(this.testShowNextLevel()){
          this.getLevel(this.lvl+1);
        }
      },
      prevLocation(){
        if(this.location > 0){
          this.location--;
          if(this.isSounds){
            clickSound.play();
          }
        }
      },
      nextLocation(){
        if(this.location < this.allLocations-1){
          this.location++;
          if(this.isSounds){
            clickSound.play();
          }
        }
      },
      selectLetter(index){
        if(this.animWordStart !== '') return;
        if(this.isSounds){
          clickSound.play();
        }
        let ind = this.selectedLetters.indexOf(index);

        if(ind >= 0){
          this.selectedLetters.splice(ind, 1);
          this.wordFromLetter = this.wordFromLetter.slice(0, ind) + this.wordFromLetter.slice(ind+1);
        }else if(ind === -1 ){
          this.selectedLetters.push(index);
          this.wordFromLetter += this.letters[index];
        }
      },
      backMenu(){
        if(this.isSounds){
          clickSound.play();
        }

        this.levels =  true;
        this.levelsAnim =  true;
        setTimeout(()=>{
          this.content = false;
        }, 500);

      },
      addTip(){
        this.tipCount++;
        localStorage.setItem('tips', this.tipCount);
        PLAYERSTATS.tips = this.tipCount;
        setStats();
      },
      getTip(){
        if(this.animWordStart !== '') return;
        if(this.tipCount < 1){
          if(showAdv && advTime){
            params({'showRewarded': 1});
            advTime = false;
            setTimeout(()=>{
              advTime = true;
            }, 180000);
            let isShowed = false;
            setTimeout(()=>{
              isShowed = true;
            }, 3000);
            let that= this;
            YSDK.adv.showFullscreenAdv({
              callbacks: {
                onClose: function() {
                  if(isShowed){
                    that.addTip();
                  }else{
                    that.showAdvTip = true;
                    that.isAdvShowed = false;
                    if(!isAdvShowed){
                      localStorage.setItem('isAdvShowed', 'true');
                      isAdvShowed = true;
                      that.isAdvShowed = true;
                      that.addTip();
                    }
                  }
                }
              }
            });
          }else{
            this.toggleShop();
          }
          return;
        }
        if(allDoneWords[this.word].length === wordsFromWords[this.word].length) return;
        this.tipCount--;
        localStorage.setItem('tips', this.tipCount);
        PLAYERSTATS.tips = this.tipCount;
        setStats();

        let arr = wordsFromWords[this.word].slice();
        for(let i = 0; i < this.doneWords.length; i++){
          arr.splice(arr.indexOf(this.doneWords[i]), 1);
        }
        let rand = Math.floor(Math.random()*arr.length);
        this.wordFromLetter = arr[rand];
        this.sendWord();
      },
      sendWord(){
        if(this.animWordStart !== '' || this.wordFromLetter.length === 0) return;
        if(this.nowWords.includes(this.wordFromLetter) && !this.doneWords.includes(this.wordFromLetter)){
          this.animWordStart = this.wordFromLetter;
          let wordFromLetter = this.wordFromLetter;
          this.wordFromLetter = '';
          this.selectedLetters = [];
          setTimeout(()=>{
            try{
              let scrollEl = document.querySelector('.animWordStart');
              scrollEl.scrollIntoView({behavior: 'smooth', block: "center", inline: "center"});
            }catch(e){
              console.log('Ошибка показа');
            }
            if(this.isSounds){
              doneWordSound.play();
            }
            this.addWord(wordFromLetter);
            this.animWord = wordFromLetter;
            this.newWord = wordFromLetter;
            localStorage.setItem('allDoneWords', JSON.stringify(allDoneWords));
            PLAYESTATE.allDoneWords = allDoneWords;
            setState();
            setTimeout(()=>{
              this.animWordStart = '';
              this.animWord = '';

              if(!this.testStars()){
                if(showAdv && advTime){
                  setTimeout(()=>{
                    showAdv();
                    this.advShowNow = true;
                    setTimeout(()=>{
                      this.advShowNow = false;
                    }, 10000)
                  }, 300)
                }
              }

            }, 1000)
          }, 100);

        }else{
          this.isBadWord = true;
          if(this.isSounds){
            wrongWordSound.play();
          }

          if(this.doneWords.includes(this.wordFromLetter)){
            this.wordWasIndex = this.nowWords.indexOf(this.wordFromLetter);
            setTimeout(()=>{
              try{
                let scrollEl = document.querySelector('.wordWas');
                scrollEl.scrollIntoView({behavior: 'smooth', block: "center", inline: "center"});
              }catch(e){
                console.log('Ошибка показа');
              }
            }, 100);

            setTimeout(()=>{
              this.wordWasIndex = -1;
            }, 1200)

          }

          setTimeout(()=>{
            this.isBadWord = false;
            this.wordFromLetter = '';
            this.selectedLetters = [];
          }, 400)
        }
      },
      testStars(){
        let stars = testStar(allDoneWords[this.word].length, wordsFromWords[this.word].length);
        if(stars > this.stars[this.lvl]){
          setLastLevel();
          this.getStar = stars;
          this.stars.splice(this.lvl, 1, stars);
          if(this.isSounds){
            starVolume.play();
          }
          setTimeout(()=>{
            this.getStar = -1;
            if(this.location > 7){
              this.tipCount += 3;
            }else if(this.location > 4){
              this.tipCount += 2;
            }else{
              this.tipCount++;
            }
            localStorage.setItem('tips', this.tipCount);
            PLAYERSTATS.tips = this.tipCount;
            setStats();

            if(showAdv && advTime){
              setTimeout(()=>{
                showAdv();
                this.showAdv++;
                this.advShowNow = true;
                setTimeout(()=>{
                  this.advShowNow = false;
                }, 10000)
              }, 300)
            }
          }, 1000);
          return true;
        }
        return false;

      },
      addWord(word){
        this.doneWords.push(word);
      },
      openLastLevel(){
        try{
          if(this.stars[lastLevel] > 0 && (lastLevel+1) !== allWords.length ) this.getLevel(lastLevel + 1, true);
          else this.getLevel(lastLevel, true);
        }catch(ignored){};
      }
    },
    mounted: function() {
      this.$nextTick(function() {
        if (doDeleteBlock) {
          if (document.querySelector(".pre-download")) {
            document.querySelector(".pre-download").remove()
          }
        }
        this.openLastLevel();
        document.addEventListener('keydown', this.pressKey)
      })
    }
  }
</script>



