  .checkbox + label:after {
    content: "";
    position: absolute;
    top: 50%;
    transform: translateY(-53%);
    right: 28px;
    width: 21px;
    height: 21px;
    border-radius: 50%;
    background: white;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.3);
    transition: 0.2s;
