      },
      getLevelByLevelAndLocation(level){
        return (this.location*21) + level;
      },
      toggleSettings(){
        this.isSettings = !this.isSettings;
      },
      addBuyTips(){
        this.tipCount += TIPS;
        localStorage.setItem('tips', this.tipCount);
        PLAYERSTATS.tips = this.tipCount;
        setStats();
      },
      buyTip(item){
        buyTips(item);
      },
      sendParams(par){
        try{
          params(par);
        }catch(ignored){}

      },
      toggleRules(){
        this.rules = !this.rules;
        this.sendParams({'openRules': 1});
      },
      toggleShop(){
        this.shop = !this.shop;
      },
      switchSounds(e){
        this.isSounds = e.target.checked;
        localStorage.setItem('sounds', this.isSounds);
      },
      closeShowAdvTip(){
        this.showAdvTip = false;
      },
      isCloseLevelShow(level){
        level--;
        if(level === 0 || (lastLevel === level-1 && this.stars[lastLevel] > 0)) return false;
        return lastLevel < level;
      },
      isLevelDone(level){
        level--;
        if(lastLevel === level && this.stars[lastLevel] === 0) return false;
        return lastLevel >= level;
      },
      openWordDescription(word){
        if(this.doneWords.includes(word)){
          this.toggleShowWordDesc();
          getWordDesc(word);
        }
      },
      pressKey(e){
        if(!this.content) return;
        let letter = e.key;
        if(letter === 'Backspace'){
          this.selectedLetters.pop();
          this.wordFromLetter = this.wordFromLetter.slice(0, -1);
        }else if(letter === 'Enter'){
          this.sendWord();
        } else{
          letter = letter.toLowerCase();
          if(lettersMap[letter]) letter = lettersMap[letter].toLowerCase();
          for(let i = 0; i < this.letters.length; i++){
            if(letter === this.letters[i] && !this.selectedLetters.includes(i)){
              this.selectedLetters.push(i);
              this.wordFromLetter += letter;
              if(this.isSounds){
                clickSound.play();
              }
              break;
            }
          }
        }
      },
      testShowNextLevel(){
        if(this.lvl < lastLevel) return true;
        return this.lvl === lastLevel && this.stars[this.lvl] > 0;
      },
      nextLevel(){
        if(this.testShowNextLevel()){
          this.getLevel(this.lvl+1);
        }
      },
      prevLocation(){
        if(this.location > 0){
          this.location--;
          if(this.isSounds){
            clickSound.play();
          }
        }
      },
      nextLocation(){
        if(this.location < this.allLocations-1){
          this.location++;
          if(this.isSounds){
            clickSound.play();
          }
        }
      },
      selectLetter(index){
        // if(this.animWordStart !== '') return;
        if(this.isSounds){
          clickSound.play();
        }
        let ind = this.selectedLetters.indexOf(index);

        if(ind >= 0){
          this.selectedLetters.splice(ind, 1);
          this.wordFromLetter = this.wordFromLetter.slice(0, ind) + this.wordFromLetter.slice(ind+1);
        }else if(ind === -1 ){
          this.selectedLetters.push(index);
          this.wordFromLetter += this.letters[index];
        }
      },
      backMenu(){
        if(this.isSounds){
          clickSound.play();
        }

        this.levels =  true;
        this.levelsAnim =  true;
        setTimeout(()=>{
          this.content = false;
        }, 500);

      },
      addTip(){
        this.tipCount++;
        localStorage.setItem('tips', this.tipCount);
        PLAYERSTATS.tips = this.tipCount;
      },
      getTip(){
        if(this.animWordStart !== '') return;
        if(this.tipCount < 1){
          if(showAdv && advTime){
            params({'showRewarded': 1});
            advTime = false;
            canShowAdv();
            setTimeout(()=>{
              advTime = true;
              canShowAdv();
            }, 190000);
            let isShowed = false;
            setTimeout(()=>{
              isShowed = true;
            }, 3000);
            let that= this;
            YSDK.adv.showFullscreenAdv({
              callbacks: {
                onClose: function() {
                  if(isShowed){
                    that.addTip();
                  }else{
                    that.showAdvTip = true;
                    that.isAdvShowed = false;
                    if(!isAdvShowed){
                      localStorage.setItem('isAdvShowed', 'true');
                      isAdvShowed = true;
                      that.isAdvShowed = true;
                      that.addTip();
                    }
                  }
                }
              }
            });
          }else{
            this.toggleShop();
          }
          return;
        }
        if(allDoneWords[this.word].length === wordsFromWords[this.word].length) return;
        this.tipCount--;
        localStorage.setItem('tips', this.tipCount);
        PLAYERSTATS.tips = this.tipCount;

        let arr = wordsFromWords[this.word].slice();
        for(let i = 0; i < this.doneWords.length; i++){
          arr.splice(arr.indexOf(this.doneWords[i]), 1);
        }
        let rand = Math.floor(Math.random()*arr.length);
        this.wordFromLetter = arr[rand];
        this.sendWord();
      },
      sendWord(){
        if(this.wordFromLetter.length === 0 || this.isBadWord) return;
        if(this.nowWords.includes(this.wordFromLetter) && !this.doneWords.includes(this.wordFromLetter)){
          this.animWordStart = this.wordFromLetter;
          let wordFromLetter = this.wordFromLetter;
          this.wordFromLetter = '';
          this.selectedLetters = [];
          setTimeout(()=>{
            try{
              let scrollEl = document.querySelector('.animWordStart');
              scrollEl.scrollIntoView({behavior: 'smooth', block: "center", inline: "center"});
            }catch(e){
              console.log('Ошибка показа');
            }
            if(this.isSounds){
              doneWordSound.play();
            }
            this.addWord(wordFromLetter);
            this.animWord = wordFromLetter;
            this.newWord = wordFromLetter;
            localStorage.setItem('allDoneWords', JSON.stringify(allDoneWords));
            PLAYESTATE.allDoneWords = allDoneWords;
            setTimeout(()=>{
              this.animWordStart = '';
              this.animWord = '';

              if(!this.testStars()){
                if(showAdv && advTime){
                  setTimeout(()=>{
                    showAdv();
                    this.advShowNow = true;
                    setTimeout(()=>{
                      this.advShowNow = false;
                    }, 10000)
                  }, 300)
                }
              }

            }, 1000)
          }, 100);

        }else{
          this.isBadWord = true;
          if(this.isSounds){
            wrongWordSound.play();
          }

          if(this.doneWords.includes(this.wordFromLetter)){
            this.wordWasIndex = this.nowWords.indexOf(this.wordFromLetter);
            setTimeout(()=>{
              try{
                let scrollEl = document.querySelector('.wordWas');
                scrollEl.scrollIntoView({behavior: 'smooth', block: "center", inline: "center"});
              }catch(e){
                console.log('Ошибка показа');
              }
            }, 100);

            setTimeout(()=>{
              this.wordWasIndex = -1;
            }, 1200)

          }

          setTimeout(()=>{
            this.isBadWord = false;
            this.wordFromLetter = '';
            this.selectedLetters = [];
          }, 400)
        }
      },
      testStars(){
        let stars = testStar(allDoneWords[this.word].length, wordsFromWords[this.word].length);
        if(stars > this.stars[this.lvl]){
          setLastLevel();
          this.getStar = stars;
          this.stars.splice(this.lvl, 1, stars);
          if(this.isSounds){
            starVolume.play();
          }
          setTimeout(()=>{
            this.getStar = -1;
            if(this.location > 5){
              this.tipCount += 2;
            }else{
              this.tipCount++;
            }
            localStorage.setItem('tips', this.tipCount);
            PLAYERSTATS.tips = this.tipCount;

            if(showAdv && advTime){
              setTimeout(()=>{
                showAdv();
                this.showAdv++;
                this.advShowNow = true;
                setTimeout(()=>{
                  this.advShowNow = false;
                }, 10000)
              }, 300)
            }
          }, 1000);
          return true;
        }
        return false;

      },
      addWord(word){
        this.doneWords.push(word);
      },
      openLastLevel(){
        try{
          this.getLevel(lastLevel, true);
        }catch(ignored){};
      }
    },
    mounted: function() {
      this.$nextTick(function() {
        if (doDeleteBlock) {
          if (document.querySelector(".pre-download")) {
            document.querySelector(".pre-download").remove()
          }
        }
        this.openLastLevel();
        document.addEventListener('keydown', this.pressKey)
      })
    }
  }
</script>


