export function whyBadWord(word){
    if(properNames.includes(word)){
        return 'Игра не принимает имена собственные, потому что их количество практически бесконечно. Некоторые слова могут показаться названиями, именами или чем-то другим, но при этом иметь нарицательное значение, поэтому обязательно узнавайте его, нажимая на слово.';
    }else if(numbers.includes(word)){
        return 'Игра не принимает имена числительные, только существительные.';
    }else if(abbrs.includes(word)){
     return 'Игра не принимает аббревиатуры.';
    }else if(pronouns.includes(word)){
        return 'Игра не принимает местоимения.';
    }else if(letters.includes(word)){
        return 'Игра не принимает названия букв. Вы можете встретить подобные слова в игре, если только у них есть другие общеупотребительные значения, кроме как название буквы.'
    } else if(whyWords[word]){
        try{
            let w = whyWords[word];
            let last = w[w.length-1];
            console.log(last);
            if(last !== '.' && last !== '!'){
                return w + '.';
            }
        }catch(e){}
        return whyWords[word];
    }
    return false;
}

