function getDescSacredBooks(book){
    return 'Слово ' + book + ' является именем собственным, так как это название священной книги.';
}
function getDescSingularNumber(word1, word2){
    return 'У слова "' + word1 + '" существует единственное число - ' + '"' + word2 +'".'
}
